import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from './axios'; 

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();  // Correctly using useNavigate() inside a Router context

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isRegistering ? '/api/register' : '/api/login';
    
    try {
      const response = await axios.post(url, { email, password });
      localStorage.setItem('token', response.data.token);
      navigate('/urls');  // Use navigate to redirect the user
    } catch (err) {
      setError('Error en el inicio de sesión o registro');
    }
  };

  return (
    <div>
      <h2>{isRegistering ? 'Registrarse' : 'Iniciar Sesión'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Correo electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">{isRegistering ? 'Registrarse' : 'Iniciar Sesión'}</button>
      </form>
      {error && <p>{error}</p>}
      <button onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? '¿Ya tienes cuenta? Iniciar sesión' : '¿No tienes cuenta? Registrarse'}
      </button>
    </div>
  );
};

export default LoginForm;
