import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import LoginForm from './LoginForm';
import UrlsPage from './UrlsPage';
import CreateUrlPage from './CreateUrlPage';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // state to track if the user is logged in
  const navigate = useNavigate();

  // Simulate login functionality
  const login = () => {
    setIsAuthenticated(true); // Set authenticated to true when user logs in
    navigate('/urls'); // Redirect to the list of URLs after login
  };

  // Simulate logout functionality
  const logout = () => {
    setIsAuthenticated(false);
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <Routes>
      {/* Default route that checks if the user is logged in */}
      <Route path="/" element={isAuthenticated ? <UrlsPage logout={logout} /> : <LoginForm login={login} />} />
      <Route path="/login" element={<LoginForm login={login} />} />
      <Route path="/urls" element={isAuthenticated ? <UrlsPage logout={logout} /> : <LoginForm login={login} />} />
      <Route path="/create-url" element={isAuthenticated ? <CreateUrlPage /> : <LoginForm login={login} />} />
    </Routes>
  );
}

export default App;
